from django.db import models

from wagtailhyper.models import WagtailHyperPage
from wagtail.core.models import Page
from wagtailhyper.fields import HyperField, HyperFieldPanel
from wagtail.admin.edit_handlers import FieldPanel
from wagtail.images.models import Image
from wagtail.images.edit_handlers import ImageChooserPanel

class HomePage(WagtailHyperPage):
    image = models.ForeignKey(
        'wagtailimages.Image',
        null=True,
        blank=True,
        on_delete=models.SET_NULL,
        related_name='+',
        help_text='Landscape mode only; horizontal width between 1000px and 3000px.'
    )
    content = HyperField(blank=True, null=True)
    content_test = HyperField(blank=True, null=True)
    content_panels = Page.content_panels + [
        ImageChooserPanel('image'),
        HyperFieldPanel('content'),
        HyperFieldPanel('content_test')
    ]