(function () {
    var PageChooserField = function (page_search_url) {
        return {
            type: 'chooser',
            label: 'Page Chooser',
            model: 'page',
            chooserButtonText: 'Choose Page',
            getItems: function(query, page, perPage, callback) {
                var str = 'q='+query + '&page='+page + '&perPage='+perPage
                fetch( page_search_url + '?' + str, {credentials: 'include'})
                .then(function(response) { return response.json() })
                .then(function(data) {
                    callback({
                        total: data.total,
                        currentPage: data.current_page,
                        result: data.result,
                        perPage:perPage
                    })
                })
            },

            displayItem: function(item) {
                return '<div class="card text-center"><div class="card-body"><h5 class="card-title">'+ item.title +'</h5></div></div>'
            },

            displaySelectedItem: function (item) {
                return '<div class="card text-center"><div class="card-body"><h5 class="card-title">'+ item.title +'</h5></div></div>'
            }
        }
    }
    var PageChooserBlock = function (pageChooser) {
        return {
            title: 'Page',
            description: 'Choose a Wagtail Page',
            default_values: {
                settings: {}
            },
            config: {
                preview: function (self, callback) {
                    callback('Wagtail Page: ' + self.title)
                },
                styles: [{id: 'default', name: 'Default'}]
            },
            settings_schema: {
                fields: [
                    pageChooser
                ]
            }
        }
    }
    var pageChooserBlock = PageChooserBlock(PageChooserField(WAGTAIL_PAGE_SEARCH_URL))
    hyperEditor.register_block('page', pageChooserBlock)
})()
