(function () {

    var SnippetChooserField = function (page_search_url) {
        return {
            type: 'chooser',
            label: 'Snippet Chooser',
            model: 'snippet',
            chooserButtonText: 'Choose Snippet',
            getItems: function(query, page, perPage, callback) {
                var str = 'q='+query + '&page='+page + '&perPage='+perPage
                fetch( page_search_url + '?' + str, {credentials: 'include'})
                .then(function(response) { return response.json() })
                .then(function(data) {
                    callback({
                        total: data.total,
                        currentPage: data.current_page,
                        result: data.result,
                    })
                })
            },

            displayItem: function(item) {
                return '<div class="card text-center"><div class="card-body"><h5 class="card-title">'+ item.title +'</h5></div></div>'
            },

            displaySelectedItem: function (item) {
                return '<div class="card text-center"><div class="card-body"><h5 class="card-title">'+ item.title +'</h5></div></div>'
            }
        }
    }

    var SnippetChooserBlock = function (snippetChooser) {
        return {
            title: 'Snippet',
            description: 'Choose a Snippet',
            default_values: {
                settings: {}
            },
            config: {
                preview: function (self, callback) {
                    callback('Snippet: ' + self.title)
                },
                styles: [{id: 'default', name: 'Default'}]
            },
            settings_schema: {
                fields: [
                    snippetChooser
                ]
            }
        }
    }

    var snippetChooserBlock = SnippetChooserBlock(SnippetChooserField(WAGTAIL_SNIPPET_SEARCH_URL))
    hyperEditor.register_block('snippet', snippetChooserBlock)

})()