(function () {
    var SvgImageBlock = function (svgChooser) {
        return {
            title: 'Svg Image',
            description: 'Choose a svg image',
            default_values: {
                extra: {},
                settings: {}
            },
            settings_schema: {
                fields: [
                    svgChooser
                ]
            },
            config: {
                styles: [
                    {id: 'default', name: 'Default'}
                ],
                preview(self, callback) {
                    callback('List')
                }
            }
        }
    }
    var svgImageBlock = SvgImageBlock(SvgChooserField(WAGTAIL_SVG_SEARCH_URL))
    hyperEditor.register_block('svg', svgImageBlock)
})()
