from wagtail.contrib.modeladmin.options import (
    ModelAdmin, modeladmin_register)
from wagtailhyper.models import MegaMenuItem, Menu, MenuItem
from wagtail.core import hooks
from .settings import ENABLED_WAGTAIL_HYPER_MEGAMENU

from wagtailhyper.blocks import add_to_block_registry
from wagtailhyper.blocks.core import StylableBlock
from .models import SvgImage
from django.urls import reverse_lazy

class MegaMenuItemAdmin(ModelAdmin):
    model = MegaMenuItem
    menu_label = 'Mega Menus'  # ditch this to use verbose_name_plural from model
    menu_icon = 'fa-snowflake-o'  # change as required
    menu_order = 200  # will put in 3rd place (000 being 1st, 100 2nd)
    add_to_settings_menu = True  # or True to add your model to the Settings sub-menu
    exclude_from_explorer = False # or True to exclude pages of this type from Wagtail's explorer view
    list_display = ('title', 'order',)
    search_fields = ('title',)

class MenuAdmin(ModelAdmin):
    model = Menu
    menu_label = 'Menus'  # ditch this to use verbose_name_plural from model
    menu_icon = 'fa-bars'  # change as required
    menu_order = 200  # will put in 3rd place (000 being 1st, 100 2nd)
    add_to_settings_menu = True  # or True to add your model to the Settings sub-menu
    exclude_from_explorer = False # or True to exclude pages of this type from Wagtail's explorer view
    list_display = ('title', 'code',)
    search_fields = ('title',)

if ENABLED_WAGTAIL_HYPER_MEGAMENU:
    modeladmin_register(MegaMenuItemAdmin)

modeladmin_register(MenuAdmin)

""" Regestering SVG as model admin """
class MyPageModelAdmin(ModelAdmin):
    model = SvgImage
    menu_label = 'SVG Image'
    menu_icon  = 'image'
    list_display = ('image', 'title')

# Now you just need to register your customised ModelAdmin class with Wagtail
modeladmin_register(MyPageModelAdmin)



# image block 
class ImageBlock(StylableBlock):
    JS_PLUGIN = 'wagtailhyper/blocks/image.js'

add_to_block_registry('image', ImageBlock)


